import React from 'react';

const AuthContext = React.createContext({
    isLoggedIn: false,
    onLogout: () => {}, //dummy function for IDE to better work
});

export const AuthContextPrivider = () => {
    const [isLoggedIn, setIsLoggedIn] = useState(false);

    const logoutHandler = () => {
        setIsLoggedIn(false);
    }

    const loginHandler = () => {
        setIsLoggedIn(true);
    }

    return <AuthContext.Provider
    value={{isLoggedIn: isLoggedIn, onLogout: logoutHandler}}>{props.children}</AuthContext.Provider>;
};

export default AuthContext;
